import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

function ProfilePage() {
  const { id } = useParams();
  const [user, setUser] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    fetch(`/api/profile/${id}`)
      .then(res => {
        if (!res.ok) throw new Error('User not found');
        return res.json();
      })
      .then(data => setUser(data))
      .catch(err => setError(err.message));
  }, [id]);

  if (error) {
    return <div style={{ padding: '2rem', color: 'red' }}>{error}</div>;
  }

  if (!user) {
    return <div style={{ padding: '2rem' }}>Loading...</div>;
  }

  return (
    <div style={styles.container}>
      <div style={styles.profileCard}>
        <img
          src={`http://localhost:8080/images/${user.profileImage}`}
          alt={`${user.username}'s profile`}
          style={styles.profileImage}
        />
        <h1 style={styles.username}>{user.username}</h1>
        <p style={styles.email}>{user.email}</p>
      </div>

      <h2 style={styles.sectionTitle}>Blogs</h2>
      <div style={styles.blogList}>
        {user.blogs.map(blog => (
          <div key={blog.id} style={styles.blogCard}>
            <img src={blog.thumbnailUrl} alt="Thumbnail" style={styles.thumbnail} />
            <div>
              <h3 style={styles.blogTitle}>{blog.title}</h3>
              <p style={styles.destination}>üìç {blog.destination}</p>
              <p style={styles.content}>{blog.content}</p>
              <p style={styles.timestamp}>üïí {new Date(blog.createdAt).toLocaleString()}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  container: {
    fontFamily: 'Arial, sans-serif',
    padding: '2rem',
    backgroundColor: '#f9f9f9',
    minHeight: '100vh',
  },
  profileCard: {
    background: 'white',
    padding: '2rem',
    borderRadius: '10px',
    boxShadow: '0 4px 10px rgba(0,0,0,0.1)',
    textAlign: 'center',
    marginBottom: '2rem',
  },
  profileImage: {
    width: '120px',
    height: '120px',
    objectFit: 'cover',
    borderRadius: '50%',
    marginBottom: '1rem',
  },
  username: {
    margin: 0,
    fontSize: '2rem',
    color: '#333',
  },
  email: {
    margin: 0,
    color: '#777',
  },
  sectionTitle: {
    fontSize: '1.5rem',
    marginBottom: '1rem',
    color: '#444',
  },
  blogList: {
    display: 'flex',
    flexDirection: 'column',
    gap: '1rem',
  },
  blogCard: {
    background: 'white',
    padding: '1rem',
    borderRadius: '10px',
    display: 'flex',
    gap: '1rem',
    boxShadow: '0 2px 6px rgba(0,0,0,0.05)',
  },
  thumbnail: {
    width: '150px',
    height: '100px',
    objectFit: 'cover',
    borderRadius: '8px',
  },
  blogTitle: {
    margin: 0,
    fontSize: '1.2rem',
    color: '#222',
  },
  destination: {
    margin: '0.3rem 0',
    fontWeight: 'bold',
    color: '#555',
  },
  content: {
    margin: 0,
    color: '#333',
  },
  timestamp: {
    marginTop: '0.5rem',
    fontSize: '0.8rem',
    color: '#999',
  },
};

export default ProfilePage;
