import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './FinalizeRoutePage.css';

const FinalizeRoutePage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { selectedDestinations } = location.state || { selectedDestinations: [] };

  return (
    <div className="finalize-route-container">
      <button className="back-btn" onClick={() => navigate(-1)}>&larr; Back</button>
      <h1 className="finalize-route-title">Your Finalized Route</h1>
      {selectedDestinations.length === 0 ? (
        <div className="finalize-route-empty">No route selected.</div>
      ) : (
        <div className="horizontal-route-container">
          <div className="horizontal-route">
            {selectedDestinations.map(({ destination, subplaces }, idx) => (
              <div key={destination.id} className="route-stop-group">
                <div className="route-dot-group">
                  <div className="route-dot" />
                  {idx !== selectedDestinations.length - 1 && <div className="route-connector" />}
                </div>
                <div className="route-label">{destination.name}</div>
                {subplaces.length > 0 && (
                  <div className="subplace-row">
                    {subplaces.map(sub => (
                      <div key={sub.id} className="subplace-dot-group">
                        <div className="subplace-dot" />
                        <div className="subplace-label">{sub.name}</div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default FinalizeRoutePage;
