import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './ProfilePage.css'; // <-- Add this line

const ProfilePage = () => {
  const { id } = useParams();
  const [user, setUser] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(`/api/profile/${id}`)
      .then((res) => {
        if (!res.ok) throw new Error('User not found');
        return res.json();
      })
      .then(setUser)
      .catch(setError);
  }, [id]);

  if (error) return <div className="error-message">{error.message}</div>;
  if (!user) return <div className="loading-message">Loading...</div>;

  return (
    <div className="profile-container">
      <div className="profile-header">
        <img
          src={`/images/${user.profileImage}`}
          alt={`${user.username}'s profile`}
          className="profile-image"
        />
        <div>
          <h1>{user.username}'s Profile</h1>
          <p className="email">{user.email}</p>
        </div>
      </div>

      <div className="section">
        <h2>Blogs</h2>
        {user.blogs.length === 0 ? (
          <p>No blogs yet.</p>
        ) : (
          <div className="blog-list">
            {user.blogs.map((blog) => (
              <div key={blog.id} className="blog-card">
                <img src={blog.thumbnailUrl} alt="thumbnail" />
                <div>
                  <h3>{blog.title}</h3>
                  <p className="destination">{blog.destination}</p>
                  <p>{blog.content.slice(0, 100)}...</p>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ProfilePage;
