import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './CreateBlogPage.css';

const API_URL = process.env.REACT_APP_URL;

const CreateBlogPage = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [destination, setDestination] = useState('');
  const [mediaFiles, setMediaFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  const [touched, setTouched] = useState({
    title: false,
    content: false,
    destination: false
  });
  const fileInputRef = useRef(null);

  useEffect(() => {
    // Debug log to see user data
    console.log('Current user data:', user);
    // Get stored user data as backup
    const storedUser = JSON.parse(localStorage.getItem('user'));
    console.log('Stored user data:', storedUser);

    // If no user data is available, redirect to login
    if (!user && !storedUser) {
      navigate('/login');
    }
  }, [user, navigate]);

  const handleMediaUpload = (event) => {
    const files = Array.from(event.target.files);
    const newMediaFiles = files.map(file => ({
      file,
      type: file.type.startsWith('image/') ? 'image' : 'video',
      preview: URL.createObjectURL(file)
    }));
    setMediaFiles([...mediaFiles, ...newMediaFiles]);
  };

  const removeMedia = (index) => {
    const newMediaFiles = [...mediaFiles];
    URL.revokeObjectURL(newMediaFiles[index].preview);
    newMediaFiles.splice(index, 1);
    setMediaFiles(newMediaFiles);
  };

  const handleBlur = (field) => {
    setTouched(prev => ({ ...prev, [field]: true }));
  };

  const validateForm = () => {
    const errors = {
      title: !title.trim() ? 'Title is required' : '',
      content: !content.trim() ? 'Content is required' : '',
      destination: !destination.trim() ? 'Destination is required' : ''
    };

    // Set all fields as touched when validating
    setTouched({
      title: true,
      content: true,
      destination: true
    });

    return errors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validateForm();
    
    if (Object.values(validationErrors).some(error => error)) {
      setError('Please fill in all required fields');
      return;
    }

    setLoading(true);
    setError('');
    setSuccess(false);

    const storedUser = JSON.parse(localStorage.getItem('user'));
    const userId = user?.id || storedUser?.id;

    if (!userId) {
      setError('User ID not found. Please log in again.');
      setLoading(false);
      return;
    }

    const token = localStorage.getItem('token');
    if (!token) {
      setError('Authentication token not found. Please log in again.');
      setLoading(false);
      return;
    }

    try {
      // For now, we'll just use the first media file as thumbnail if available
      const thumbnailUrl = mediaFiles.length > 0 ? mediaFiles[0].preview : null;

      const blogData = {
        userId: userId,
        title: title.trim(),
        content: content.trim(),
        customDestinations: [destination.trim()],
        thumbnailUrl: thumbnailUrl,
        status: "published"
      };

      const response = await fetch(`${API_URL}/api/blogs`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(blogData),
      });

      if (response.ok) {
        setSuccess(true);
        setTitle('');
        setContent('');
        setDestination('');
        setMediaFiles([]);
        setTimeout(() => navigate('/profile'), 2000);
      } else {
        const data = await response.json();
        setError(data.message || 'Failed to create blog post');
      }
    } catch (err) {
      console.error('Error creating blog:', err);
      setError('Network error. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // If no user data is available, show login message
  if (!user && !JSON.parse(localStorage.getItem('user'))) {
    return (
      <div className="create-blog-container">
        <div className="create-blog-card">
          <h2>Please log in to create a blog post</h2>
        </div>
      </div>
    );
  }

  const showError = (field) => {
    return touched[field] && !eval(field).trim();
  };

  return (
    <div className="create-blog-container">
      <div className="create-blog-card">
        <div className="post-header">
          <img 
            src={user?.profile_image || 'https://via.placeholder.com/40'} 
            alt="Profile" 
            className="profile-pic"
          />
          <div className="user-info">
            <h3>{user?.username || 'User'}</h3>
            <div className={`destination-input ${showError('destination') ? 'error' : ''}`}>
              <span className="location-icon">📍</span>
              <input
                type="text"
                value={destination}
                onChange={(e) => setDestination(e.target.value)}
                onBlur={() => handleBlur('destination')}
                placeholder="Where are you? *"
                className="transparent-input"
              />
            </div>
            {showError('destination') && (
              <span className="field-error">Destination is required</span>
            )}
          </div>
        </div>

        <form onSubmit={handleSubmit} className="post-form">
          <div className={`title-input-container ${showError('title') ? 'error' : ''}`}>
            <input
              type="text"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              onBlur={() => handleBlur('title')}
              placeholder="Give your post a title *"
              className="title-input"
            />
            {showError('title') && (
              <span className="field-error">Title is required</span>
            )}
          </div>
          
          <div className={`content-input-container ${showError('content') ? 'error' : ''}`}>
            <textarea
              value={content}
              onChange={(e) => setContent(e.target.value)}
              onBlur={() => handleBlur('content')}
              placeholder="What's on your mind? *"
              className="content-input"
            />
            {showError('content') && (
              <span className="field-error">Content is required</span>
            )}
          </div>

          {mediaFiles.length > 0 && (
            <div className="media-preview-grid">
              {mediaFiles.map((media, index) => (
                <div key={index} className="media-preview-container">
                  {media.type === 'image' ? (
                    <img src={media.preview} alt={`Preview ${index}`} className="media-preview" />
                  ) : (
                    <video src={media.preview} className="media-preview" controls />
                  )}
                  <button 
                    type="button" 
                    className="remove-media-btn"
                    onClick={() => removeMedia(index)}
                  >
                    ×
                  </button>
                </div>
              ))}
            </div>
          )}

          <div className="post-actions">
            <div className="media-buttons">
              <button 
                type="button" 
                className="media-btn"
                onClick={() => fileInputRef.current.click()}
              >
                <span className="media-icon">🖼️</span>
                Photo/Video
              </button>
              <input
                ref={fileInputRef}
                type="file"
                accept="image/*,video/*"
                multiple
                onChange={handleMediaUpload}
                style={{ display: 'none' }}
              />
            </div>

            {error && <div className="error-message">{error}</div>}
            {success && (
              <div className="success-message">
                Post created successfully! Redirecting...
              </div>
            )}

            <button 
              type="submit" 
              className="post-button"
              disabled={loading}
            >
              {loading ? 'Posting...' : 'Post'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreateBlogPage;
