import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

function ProfilePage() {
  const { id } = useParams();
  const [profileData, setProfileData] = useState(null);

  useEffect(() => {
    // Fetch profile data from your backend API using the user id
    fetch(`http://localhost:8080/api/profile/${id}`)
      .then(res => res.json())
      .then(data => setProfileData(data))
      .catch(err => console.error('Error fetching profile:', err));
  }, [id]);

  if (!profileData) return <div>Loading profile...</div>;

  return (
    <div>
      <h1>{profileData.username}'s Profile</h1>
      {/* Render other profile info and blogs */}
      <div>
        <h2>Blogs</h2>
        {profileData.blogs && profileData.blogs.length > 0 ? (
          profileData.blogs.map(blog => (
            <div key={blog.id}>
              <h3>{blog.title}</h3>
              <p>{blog.content}</p>
            </div>
          ))
        ) : (
          <p>No blogs found.</p>
        )}
      </div>
    </div>
  );
}

export default ProfilePage;
