import React, { useState } from 'react';
import './CreateTourPage.css';

const CreateTourPage = () => {
  const [search, setSearch] = useState('');
  const [subplaces, setSubplaces] = useState([]);

  const handleSearchChange = async (e) => {
    const query = e.target.value;
    setSearch(query);

    if (query.length >= 3) {
      try {
        const response = await fetch(`http://localhost:8080/api/subplaces?destination=${encodeURIComponent(query)}`);
        if (!response.ok) throw new Error('Failed to fetch');
        const data = await response.json();
        setSubplaces(data);
        console.log(data);
      } catch (error) {
        console.error('Error fetching subplaces:', error);
        setSubplaces([]);
      }
    } else {
      setSubplaces([]);
    }
  };

  return (
    <div className="create-tour-container">
      <h1>Create a Tour</h1>
      <input
        type="text"
        value={search}
        onChange={handleSearchChange}
        placeholder="Search destination..."
        className="search-box"
      />

      <ul className="subplace-list">
        {subplaces.map((place) => (
          <li key={place.id}>{place.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default CreateTourPage;
