import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const ProfilePage = () => {
  const { id } = useParams(); // Extract dynamic id from URL
  const [user, setUser] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`/api/profile/${id}`) //Use dynamic id
      .then((res) => {
        if (!res.ok) throw new Error('User not found');
        return res.json();
      })
      .then((userData) => {
        setUser(userData);
        setLoading(false);
      })
      .catch((err) => {
        setError(err);
        setLoading(false);
      });
  }, [id]);

  const handleCreateTour = () => {
    // Navigate to tour creation page
    window.location.href = '/create-tour';
  };

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="bg-white p-8 rounded-2xl shadow-lg text-center">
          <div className="text-red-500 text-6xl mb-4">⚠️</div>
          <h2 className="text-2xl font-bold text-gray-800 mb-2">Oops! Something went wrong</h2>
          <p className="text-gray-600">{error.message}</p>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-4 border-blue-500 border-t-transparent mx-auto mb-4"></div>
          <p className="text-gray-600 text-lg">Loading your profile...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hero Section */}
      <div className="bg-gradient-to-r from-blue-600 to-purple-700 text-white">
        <div className="container mx-auto px-6 py-12">
          <div className="flex flex-col md:flex-row items-center gap-8">
            <div className="relative">
              <img
                src={user.profileImage ? `/images/${user.profileImage}` : "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=150&h=150&fit=crop&crop=face"}
                alt={`${user.username}'s profile`}
                className="w-32 h-32 rounded-full object-cover border-4 border-white shadow-xl"
              />
              <div className="absolute -bottom-2 -right-2 bg-green-500 w-8 h-8 rounded-full border-4 border-white"></div>
            </div>
            <div className="text-center md:text-left">
              <h1 className="text-4xl font-bold mb-2">{user.username}</h1>
              <p className="text-blue-100 text-lg mb-4">{user.email}</p>
              <div className="flex flex-wrap gap-2 text-sm">
                <span className="bg-white/20 px-3 py-1 rounded-full">Travel Enthusiast</span>
                <span className="bg-white/20 px-3 py-1 rounded-full">Blogger</span>
                <span className="bg-white/20 px-3 py-1 rounded-full">Adventure Seeker</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="container mx-auto px-6 py-12">
        {/* Create Tour CTA */}
        <div className="mb-12 text-center">
          <button
            onClick={handleCreateTour}
            className="bg-gradient-to-r from-orange-500 to-pink-500 hover:from-orange-600 hover:to-pink-600 text-white font-bold py-4 px-8 rounded-2xl text-lg shadow-lg transform hover:scale-105 transition-all duration-200 inline-flex items-center gap-3"
          >
            <span className="text-2xl">✈️</span>
            Create New Tour
          </button>
          <p className="text-gray-600 mt-3">Share your travel experiences with the world</p>
        </div>

        {/* Blog Posts Section */}
        <section className="mb-16">
          <div className="flex items-center justify-between mb-8">
            <h2 className="text-3xl font-bold text-gray-800 flex items-center gap-3">
              <span className="text-blue-500">📝</span>
              My Travel Blogs
            </h2>
            <span className="bg-blue-100 text-blue-600 px-3 py-1 rounded-full text-sm font-medium">
              {user.blogs.length} posts
            </span>
          </div>

          {user.blogs.length === 0 ? (
            <div className="text-center py-16">
              <div className="text-6xl mb-4">📖</div>
              <h3 className="text-xl font-semibold text-gray-700 mb-2">No blogs yet</h3>
              <p className="text-gray-500">Start sharing your travel stories!</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
              {user.blogs.map((blog) => (
                <article key={blog.id} className="bg-white rounded-2xl shadow-lg overflow-hidden hover:shadow-xl transition-all duration-300 transform hover:-translate-y-2">
                  <div className="relative">
                    <img
                      src={blog.thumbnailUrl || "https://images.unsplash.com/photo-1488646953014-85cb44e25828?w=400&h=250&fit=crop"}
                      alt={blog.title}
                      className="w-full h-48 object-cover"
                    />
                    <div className="absolute top-4 left-4 bg-white/90 backdrop-blur-sm px-3 py-1 rounded-full text-sm font-medium text-blue-600">
                      {blog.destination}
                    </div>
                  </div>
                  <div className="p-6">
                    <h3 className="text-xl font-bold text-gray-800 mb-3 line-clamp-2">{blog.title}</h3>
                    <p className="text-gray-600 mb-4 line-clamp-3">{blog.content}</p>
                    <button className="text-blue-500 hover:text-blue-600 font-medium inline-flex items-center gap-2 group">
                      Read More
                      <span className="transform group-hover:translate-x-1 transition-transform">→</span>
                    </button>
                  </div>
                </article>
              ))}
            </div>
          )}
        </section>

        {/* Popular Tours Section */}
        {user.popularTours && user.popularTours.length > 0 && (
        <section>
          <div className="flex items-center justify-between mb-8">
            <h2 className="text-3xl font-bold text-gray-800 flex items-center gap-3">
              <span className="text-green-500">🌟</span>
              Popular Tour Packages
            </h2>
            <span className="bg-green-100 text-green-600 px-3 py-1 rounded-full text-sm font-medium">
              Trending
            </span>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {user.popularTours.map((tour) => (
              <div key={tour.id} className="bg-white rounded-2xl shadow-lg overflow-hidden hover:shadow-xl transition-all duration-300 transform hover:-translate-y-2">
                <div className="relative">
                  <img
                    src={tour.image || "https://images.unsplash.com/photo-1488646953014-85cb44e25828?w=400&h=250&fit=crop"}
                    alt={tour.title}
                    className="w-full h-48 object-cover"
                  />
                  {tour.rating && (
                    <div className="absolute top-4 right-4 bg-yellow-400 text-yellow-900 px-2 py-1 rounded-full text-sm font-bold flex items-center gap-1">
                      ⭐ {tour.rating}
                    </div>
                  )}
                </div>
                <div className="p-6">
                  <h3 className="text-xl font-bold text-gray-800 mb-2">{tour.title}</h3>
                  <div className="flex justify-between items-center mb-4">
                    {tour.price && <span className="text-2xl font-bold text-green-600">{tour.price}</span>}
                    {tour.duration && (
                      <span className="text-gray-500 bg-gray-100 px-3 py-1 rounded-full text-sm">
                        {tour.duration}
                      </span>
                    )}
                  </div>
                  <button className="w-full bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700 text-white font-semibold py-3 px-6 rounded-xl transition-all duration-200 transform hover:scale-105">
                    View Details
                  </button>
                </div>
              </div>
            ))}
          </div>
        </section>
        )}
      </div>
    </div>
  );
};

export default ProfilePage;
