import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './ProfilePage.css';

const API_URL = 'http://localhost:8080';

const ProfilePage = () => {
  const navigate = useNavigate();
  const { user: authUser, logout } = useAuth();
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('blogs');

  // Sample interests - this should come from your backend
  const interests = ['Travel Photography', 'Adventure', 'Cultural Exploration', 'Food Tourism', 'Eco Travel'];

  useEffect(() => {
    const fetchUser = async () => {
      try {
        setLoading(true);
        const res = await fetch(`${API_URL}/api/profile/${authUser.id}`);
        if (!res.ok) throw new Error('Failed to fetch user');
        const data = await res.json();
        setUser(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    if (authUser && authUser.id) {
      fetchUser();
    }
  }, [authUser]);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  if (loading) {
    return (
      <div className="profile-loading">
        <div className="profile-spinner"></div>
        <p>Loading profile...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="profile-error">
        <p>‚ùå {error}</p>
        <button onClick={() => window.location.reload()}>Retry</button>
      </div>
    );
  }

  if (!user) return null;

  return (
    <div className="profile-container">
      {/* Cover Photo Section */}
      <div className="profile-cover-section">
        <img
          src="https://images.unsplash.com/photo-1469854523086-cc02fe5d8800"
          alt="Cover"
          className="profile-cover-photo"
        />
        <div className="profile-avatar-container">
          <img
            src={user.profile_image || 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d'}
            alt="Profile"
            className="profile-avatar"
          />
        </div>
      </div>

      {/* Main Content */}
      <div className="profile-content">
        {/* Sidebar Navigation */}
        <div className="profile-sidebar">
          <nav>
            <div className="nav-item" onClick={() => navigate('/create-tour')}>
              <span className="nav-item-icon">‚úàÔ∏è</span>
              Create Tour
            </div>
            <div className="nav-item" onClick={() => navigate('/create-blog')}>
              <span className="nav-item-icon">üìù</span>
              Write Blog
            </div>
            <div className="nav-item">
              <span className="nav-item-icon">‚öôÔ∏è</span>
              Edit Profile
            </div>
            <div className="nav-item" onClick={handleLogout}>
              <span className="nav-item-icon">üö™</span>
              Logout
            </div>
          </nav>
        </div>

        {/* Main Profile Section */}
        <div className="profile-main">
          {/* Profile Header */}
          <div className="profile-header">
            <h1 className="profile-name">{user.username}</h1>
            <p className="profile-bio">{user.bio || 'No bio yet'}</p>
            
            {/* Profile Stats */}
            <div className="profile-stats">
              <div className="stat-item">
                <span className="stat-number">{user.tours?.length || 0}</span>
                <span className="stat-label">Tours</span>
              </div>
              <div className="stat-item">
                <span className="stat-number">{user.blogSummaries?.length || 0}</span>
                <span className="stat-label">Blogs</span>
              </div>
              <div className="stat-item">
                <span className="stat-number">{user.saved?.length || 0}</span>
                <span className="stat-label">Saved</span>
              </div>
            </div>
          </div>

          {/* Interests Section */}
          <div className="interests-section">
            <h2 className="interests-title">Interests</h2>
            <div className="interests-list">
              {interests.map((interest, index) => (
                <span key={index} className="interest-tag">{interest}</span>
              ))}
            </div>
          </div>

          {/* Tours Section */}
          <div className="content-section">
            <h2 className="section-title">Recent Tours</h2>
            <div className="content-grid">
              {user.tours?.slice(0, 4).map(tour => (
                <div key={tour.id} className="content-card">
                  <img
                    src="https://images.unsplash.com/photo-1488646953014-85cb44e25828"
                    alt={tour.title}
                    className="card-image"
                  />
                  <div className="card-content">
                    <h3 className="card-title">{tour.title}</h3>
                    <p className="card-description">
                      Status: {tour.status} ‚Ä¢ {tour.startDate ? `From ${tour.startDate}` : 'No dates'}
                    </p>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Blogs Section */}
          <div className="content-section">
            <h2 className="section-title">Recent Blogs</h2>
            <div className="content-grid">
              {user.blogSummaries?.slice(0, 4).map(blog => (
                <div key={blog.id} className="content-card" onClick={() => navigate(`/blogs/${blog.id}`)}>
                  <img
                    src="https://images.unsplash.com/photo-1488646953014-85cb44e25828"
                    alt={blog.title}
                    className="card-image"
                  />
                  <div className="card-content">
                    <h3 className="card-title">{blog.title}</h3>
                    <p className="card-description">
                      {blog.destination || 'No destination'}
                    </p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
