import React, { createContext, useContext, useState, useEffect, useCallback } from 'react';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [lastActivity, setLastActivity] = useState(Date.now());

  const logout = useCallback(async () => {
    const token = localStorage.getItem('token');
    
    // Call backend logout endpoint
    if (token) {
      try {
        await fetch('http://localhost:8080/api/auth/logout', {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        });
      } catch (error) {
        console.error('Logout error:', error);
      }
    }
    
    setUser(null);
    localStorage.removeItem('user');
    localStorage.removeItem('token');
  }, []);

  const validateSession = useCallback(async (token) => {
    try {
      console.log('Validating session with token:', token);
      const response = await fetch('http://localhost:8080/api/auth/validate', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      console.log('Session validation response status:', response.status);

      if (response.ok) {
        const userData = await response.json();
        console.log('Session validation successful:', userData);
        return userData;
      } else {
        // Session is invalid or expired
        console.log('Session validation failed - session expired');
        return null;
      }
    } catch (error) {
      console.error('Session validation error:', error);
      // For network errors, we should still return null to force logout
      // This ensures that if the backend is down, we don't keep users logged in
      return null;
    }
  }, []);

  // Track user activity
  useEffect(() => {
    if (!user) return;

    const updateActivity = () => {
      setLastActivity(Date.now());
    };

    // Add event listeners for user activity
    window.addEventListener('mousemove', updateActivity);
    window.addEventListener('click', updateActivity);
    window.addEventListener('keypress', updateActivity);
    window.addEventListener('scroll', updateActivity);

    return () => {
      window.removeEventListener('mousemove', updateActivity);
      window.removeEventListener('click', updateActivity);
      window.removeEventListener('keypress', updateActivity);
      window.removeEventListener('scroll', updateActivity);
    };
  }, [user]);

  // Check session on user activity (but not too frequently)
  useEffect(() => {
    if (!user) return;

    const checkSessionOnActivity = async () => {
      const token = localStorage.getItem('token');
      if (token) {
        const userData = await validateSession(token);
        if (!userData) {
          console.log('Session expired during activity - logging out');
          await logout();
          window.location.href = '/login';
        }
      }
    };

    // Only check session if user has been active in the last 30 seconds
    const timeSinceActivity = Date.now() - lastActivity;
    if (timeSinceActivity < 30000) {
      // Debounce the check to avoid too many requests
      const timeoutId = setTimeout(checkSessionOnActivity, 1000);
      return () => clearTimeout(timeoutId);
    }
  }, [lastActivity, user, validateSession, logout]);

  useEffect(() => {
    const initializeAuth = async () => {
      try {
        // Check if user is logged in on app start
        const storedUser = localStorage.getItem('user');
        const token = localStorage.getItem('token');
        
        console.log('Initializing auth - storedUser:', !!storedUser, 'token:', !!token);
        
        if (storedUser && token) {
          // Validate the session with the backend
          const userData = await validateSession(token);
          
          if (userData) {
            console.log('Setting user from session validation');
            setUser(userData);
          } else {
            // Session validation failed - clear everything and logout
            console.log('Session validation failed - logging out user');
            localStorage.removeItem('user');
            localStorage.removeItem('token');
            setUser(null);
          }
        } else {
          console.log('No stored user or token found');
          setUser(null);
        }
      } catch (error) {
        console.error('Error during auth initialization:', error);
        setUser(null);
      } finally {
        setLoading(false);
      }
    };

    initializeAuth();
  }, [validateSession]);

  const login = (userData, token) => {
    console.log('Login successful - setting user and token');
    setUser(userData);
    localStorage.setItem('user', JSON.stringify(userData));
    localStorage.setItem('token', token);
  };

  const value = {
    user,
    login,
    logout,
    loading
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
