import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './CreateBlogPage.css';

//const API_URL = 'http://20.40.57.81:8080'; // Add API URL configuration
const API_URL = 'http://localhost:8080'; 

const CreateBlogPage = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess(false);

    try {
      const response = await fetch(`${API_URL}/api/blogs`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          title,
          content,
          user: { id: user.userId }
        }),
      });

      if (response.ok) {
        setSuccess(true);
        // Clear form
        setTitle('');
        setContent('');
        // Redirect to profile after 2 seconds
        setTimeout(() => navigate('/profile'), 2000);
      } else {
        const data = await response.json();
        setError(data.message || 'Failed to create blog post');
      }
    } catch (err) {
      setError('Network error. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  if (!user) {
    return (
      <div className="create-blog-container">
        <div className="create-blog-card">
          <h2>Please log in to create a blog post</h2>
        </div>
      </div>
    );
  }

  return (
    <div className="create-blog-container">
      <div className="create-blog-card">
        <h1>Create New Blog Post</h1>
        <p className="subtitle">Share your thoughts and experiences</p>

        <form onSubmit={handleSubmit} className="blog-form">
          <div className="form-group">
            <label htmlFor="title">Title</label>
            <input
              type="text"
              id="title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              placeholder="Enter your blog title"
              required
              maxLength="100"
            />
          </div>

          <div className="form-group">
            <label htmlFor="content">Content</label>
            <textarea
              id="content"
              value={content}
              onChange={(e) => setContent(e.target.value)}
              placeholder="Write your blog content here..."
              required
              rows="10"
            />
          </div>

          {error && <div className="error-message">{error}</div>}
          {success && (
            <div className="success-message">
              Blog post created successfully! Redirecting...
            </div>
          )}

          <button 
            type="submit" 
            className="submit-button"
            disabled={loading}
          >
            {loading ? 'Creating...' : 'Create Post'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default CreateBlogPage;
