            <div className="text-center md:text-left">
              <h1 className="text-4xl font-bold mb-2">{user.username}</h1>
              <p className="text-blue-100 text-lg mb-4">{user.email}</p>
              <div className="flex flex-wrap gap-2 text-sm">
                <span className="bg-white/20 px-3 py-1 rounded-full">Travel Enthusiast</span>
                <span className="bg-white/20 px-3 py-1 rounded-full">Blogger</span>
                <span className="bg-white/20 px-3 py-1 rounded-full">Adventure Seeker</span>
