        {/* Create Tour CTA */}
        <div className="mb-12 text-center">
          <button
            onClick={handleCreateTour}
            className="bg-gradient-to-r from-orange-500 to-pink-500 hover:from-orange-600 hover:to-pink-600 text-white font-bold py-4 px-8 rounded-2xl text-lg shadow-lg transform hover:scale-105 transition-all duration-200 inline-flex items-center gap-3"
          >
            <span className="text-2xl">‚úàÔ∏è</span>
